import React, { useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import {
  fetchCountries,
  filterByRegion,
  loadMore,
} from "../features/countries/countrySlice";
import { Container, Row, Col, Button } from "react-bootstrap";

const Home = () => {
  const dispatch = useDispatch();
  const { filtered, visibleCount, region } = useSelector(
    (state) => state.countries
  );

  useEffect(() => {
    dispatch(fetchCountries());
  }, [dispatch]);

  return (
    <Container className="home-container">
      <h2 className="text-center">WELCOME</h2>
      <div className="filter-tabs text-center my-3">
        {["All", "Asia", "Europe"].map((r) => (
          <Button
            key={r}
            variant={region === r ? "dark" : "outline-dark"}
            onClick={() => dispatch(filterByRegion(r))}
            className="mx-1"
          >
            {r}
          </Button>
        ))}
      </div>
      <Row>
        {filtered.slice(0, visibleCount).map((c, i) => (
          <Col xs={12} md={6} lg={4} key={i} className="mb-3">
            <div className="country-card border rounded p-2 text-center">
              <img src={c.flag} alt={c.name} className="flag-img mb-2" />
              <h5>{c.name}</h5>
              <p className="text-muted">{c.region}</p>
            </div>
          </Col>
        ))}
      </Row>
      {visibleCount < filtered.length && (
        <div className="text-center">
          <Button variant="dark" onClick={() => dispatch(loadMore())}>
            Load more
          </Button>
        </div>
      )}
    </Container>
  );
};

export default Home;
