import React, { useEffect, useState } from "react";
import { useSelector, useDispatch } from "react-redux";
import {
  fetchCountries,
  filterByRegion,
  loadMore,
  selectFilteredCountries,
} from "../../features/countrySlice";
import { Container, Row, Col, Button } from "react-bootstrap";
import "./Home.css";

const Home = () => {
  const dispatch = useDispatch();
  const countries = useSelector(selectFilteredCountries);
  const visibleCount = useSelector((state) => state.countries.visibleCount);
  const [activeFilter, setActiveFilter] = useState("All");
  const [currentIndex, setCurrentIndex] = useState(1); // Start from second item
  const [leftImage, setLeftImage] = useState(null);
  const [rightImage, setRightImage] = useState(null);

  useEffect(() => {
    dispatch(fetchCountries());
    if (countries.length >= 2) {
      setLeftImage(countries[0]?.flag);
      setRightImage(countries[1]?.flag);
      setCurrentIndex(1);
    }
  }, [dispatch]);

  const handleFilterClick = (region) => {
    setActiveFilter(region);
    dispatch(filterByRegion(region));
  };

  const handleLoadMore = () => {
    dispatch(loadMore());
  };

  const getSliderItems = () => {
    const slides = [];
    const maxSlides = 3;
    for (let i = 0; i < Math.min(countries.length, maxSlides * 2); i += 2) {
      slides.push({
        main: countries[i]?.flag,
        frame: countries[i + 1]?.flag || countries[0]?.flag,
      });
    }
    return slides;
  };

  const sliderItems = getSliderItems();

  const handleLeftArrow = () => {
    if (currentIndex + 1 < countries.length) {
      setLeftImage(rightImage);
      const nextIndex = currentIndex + 1;
      setRightImage(countries[nextIndex]?.flag);
      setCurrentIndex(nextIndex);
    }
  };

  const handleRightArrow = () => {
    if (currentIndex - 1 > 0) {
      setRightImage(leftImage);
      const prevIndex = currentIndex - 1;
      setLeftImage(countries[prevIndex - 1]?.flag);
      setCurrentIndex(prevIndex);
    }
  };

  return (
    <div className="home-page">
      <header className="top-header">
        <h5>Countries</h5>
        <div className="region-filters">
          {["All", "Asia", "Europe"].map((region) => (
            <button
              key={region}
              className={`region-button ${
                activeFilter === region ? "active" : ""
              }`}
              onClick={() => handleFilterClick(region)}
            >
              {region}
            </button>
          ))}
        </div>
      </header>

      <section className="welcome-section">
        <div className="line" />
        <h2>WELCOME</h2>
        <div className="line" />
      </section>

      {sliderItems.length > 0 && (
        <section className="custom-slider-section">
          <div className="slider-row">
            <div className="slider-left">
              {leftImage && (
                <img
                  src={leftImage}
                  alt="Left Flag"
                  className="slider-main-image"
                />
              )}
            </div>
            <div className="slider-right">
              {rightImage && (
                <img
                  src={rightImage}
                  alt="Right Flag"
                  className="slider-frame-image"
                />
              )}
            </div>
          </div>

          <div className="slider-controls">
            <span onClick={handleRightArrow} className="slider-arrow">
              &#8592;
            </span>
            <span className="dot active"></span>
            <span className="dot"></span>
            <span className="dot"></span>
            <span onClick={handleLeftArrow} className="slider-arrow">
              &#8594;
            </span>
          </div>
        </section>
      )}

      <Container className="countries-container">
        <Row>
          {countries.slice(0, visibleCount).map((country, idx) => (
            <Col md={6} key={idx} className="mb-4">
              <div className="country-card">
                <img src={country.flag} alt={country.name} className="flag" />
                <div className="country-details">
                  <strong>{country.name}</strong>
                  <div className="region">{country.region}</div>
                </div>
              </div>
            </Col>
          ))}
        </Row>
        {visibleCount < countries.length && (
          <div className="text-center mt-3">
            <Button variant="dark" onClick={handleLoadMore}>
              Load more
            </Button>
          </div>
        )}
      </Container>

      <footer className="footer">
        <div className="footer-icons">
          <i className="bi bi-facebook" />
          <i className="bi bi-twitter" />
          <i className="bi bi-instagram" />
          <i className="bi bi-github" />
        </div>
        <p>example@email.com</p>
        <p>Copyright Â© 2020 Name. All rights reserved.</p>
      </footer>
    </div>
  );
};

export default Home;
